import React, { useEffect } from 'react';
import {
  FlatList,
  Image,
  View,
  StyleSheet,
  Text,
  TouchableOpacity,
  ScrollView,
  Dimensions,
  Modal,
  TextInput,
} from 'react-native';
import { decks } from '../code/data';
import { selectedDeck } from '../code/data';
import { ITEM_WIDTH } from '../code/carouselCardItem';

export const getWidth = Dimensions.get('window').width + 8;
export const iwidth = Math.round(getWidth * 0.7);

export default function PreviewScreen({ navigation }) {
  const [selectedCard, setSelectedCard] = React.useState();
  const [newQuestion, setNewQuestion] = React.useState();
  const [newAnswer, setNewAnswer] = React.useState();
  const [isModalVisibleFlashcard, setIsModalVisibleFlashcard] = React.useState(false);

  const handleDeleteCard = () => {
    index = selectedDeck.flashcards.indexOf(selectedCard);
    selectedDeck.flashcards.splice(index, 1);
    alert('TEST');
  };

  const handleEditCard = () => {
    selectedCard.frontContent = newQuestion;
    selectedCard.backContent = newAnswer;
    toggleModalFlashcard();
  };

  const toggleModalFlashcard = () => {
    setIsModalVisibleFlashcard(!isModalVisibleFlashcard);
  };

  const getAnswerById = (questionId) => {
    const answerItem = answersData.find((item) => item.id === questionId);
    return answerItem ? answerItem.content : '';
  };

  const questionsData = selectedDeck.flashcards.map((card) => ({
    id: card.id,
    content: card.frontContent,
  }));

  const answersData = selectedDeck.flashcards.map((card) => ({
    id: card.id,
    content: card.backContent,
  }));

  const renderQuestionItem = ({ item }) => (
    <View style={styles.viewPadding}>
      <TouchableOpacity style={styles.deckContainer} onPress={() => setSelectedCard(item)}>
        <View style={styles.info}>
          <Text style={styles.infotext}>{selectedDeck.name}</Text>
          <View style={styles.info2}>
            <Text style={styles.infotext2}>{item.content}</Text>
            <Text style={styles.infotext2}>{getAnswerById(item.id)}</Text>
          </View>
        </View>
      </TouchableOpacity>
    </View>
  );

  return (
    <ScrollView contentContainerStyle={styles.scrollContainer} showsVerticalScrollIndicator={false}>
      <View style={styles.container}>
        <Text style={styles.subtext}>{selectedDeck.course}</Text>
        <Text style={styles.titletext}>{selectedDeck.name}</Text>
        <Text style={styles.subtext2}>Flashcard</Text>
        <TouchableOpacity style={{ paddingTop: 10, width: 100 }}>
          <Text style={styles.buttonText}>Mark as Favorite</Text>
        </TouchableOpacity>
      </View>

      <View style={styles.alignedContainer}>
        <TouchableOpacity style={styles.minibutton} onPress={() => navigation.navigate('Add Card')}>
          <Text style={styles.buttonText2}>Add</Text>
        </TouchableOpacity>

        <TouchableOpacity style={styles.minibutton} onPress={() => toggleModalFlashcard()}>
          <Text style={styles.buttonText2}>Edit</Text>
        </TouchableOpacity>
        <Modal
          animationType="slide"
          transparent={true}
          visible={isModalVisibleFlashcard}
          onRequestClose={() => {
            toggleModalFlashcard();
          }}
        >
          <View style={styles.modalContainer}>
            <View style={styles.modalContent}>
              <Text style={styles.modalTitle}>Edit Flashcard</Text>
              <TextInput
                style={styles.modalInput}
                placeholder="Question"
                value={newQuestion}
                onChangeText={(text) => setNewQuestion(text)}
              />
              <TextInput
                style={styles.modalInput}
                placeholder="Answer"
                value={newAnswer}
                onChangeText={(text) => setNewAnswer(text)}
              />
              <TouchableOpacity style={styles.modalButton} onPress={() => handleEditCard()}>
                <Text style={styles.modalButtonText}>Edit Flashcard</Text>
              </TouchableOpacity>
              <TouchableOpacity style={styles.modalButton} onPress={toggleModalFlashcard}>
                <Text style={styles.modalButtonText}>Cancel</Text>
              </TouchableOpacity>
            </View>
          </View>
        </Modal>

        <TouchableOpacity style={styles.minibutton} onPress={() => handleDeleteCard()}>
          <Text style={styles.buttonText2}>Delete</Text>
        </TouchableOpacity>

        <TouchableOpacity style={styles.minibutton}>
          <Text style={styles.buttonText2}>Play</Text>
        </TouchableOpacity>

        <TouchableOpacity style={styles.minibutton} onPress={() => navigation.navigate('Main')}>
          <Text style={styles.buttonText2}>Back</Text>
        </TouchableOpacity>
      </View>

      <FlatList
        style={styles.verticalFlatList}
        showsVerticalScrollIndicator={false}
        data={questionsData}
        scrollEnabled={false}
        renderItem={renderQuestionItem}
      />
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  scrollContainer: {
    backgroundColor: '#ECE3CE',
    paddingTop: 20,
  },
  container: {
    justifyContent: 'space-evenly',
    padding: 30,
  },
  verticalFlatList: {
    paddingBottom: 100,
    // Add any other styles you need for the FlatList
  },
  minibutton: {
    paddingVertical: 15,
    paddingHorizontal: 10,
    borderRadius: 3,
    margin: 3,
  },
  buttonText: {
    fontSize: 25,
    fontWeight: 'bold',
    color: '#white',
    backgroundColor: 'rgba(79, 111, 82, 0.7)',
    width: 200,
  },
  buttonText2: {
    fontSize: 15,
    fontWeight: 'bold',
    color: '#526D84',
  },
  viewPadding: {
    padding: 10,
  },
  deckContainer: {
    width: iwidth,
    height: 170,
    marginHorizontal: 8,
    borderRadius: 0,
    backgroundColor: '#52D681',
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  info: {
    flex: 1,
    padding: 10,
    top: 5,
    position: 'absolute',
  },
  info2: {
    flex: 1,
    padding: 10,
    top: 70,
    left: 0,
    position: 'absolute',
  },
  infotext: {
    fontSize: 18,
    color: '#4F6F52',
  },
  infotext2: {
    fontSize: 12,
    color: '#4F6F52',
  },
  subtext: {
    fontSize: 20,
    color: '#4F6F52',
  },
  subtext2: {
    fontSize: 25,
    color: '#4F6F52',
  }
})
