import * as React from 'react';
import { ImageBackground, StyleSheet, Text, TextInput, TouchableOpacity, View, FlatList } from 'react-native';
import Icon from 'react-native-vector-icons/Ionicons';

import { SLIDER_WIDTH, ITEM_WIDTH } from '../code/carouselCardItem';
import { decks } from '../code/data';
import Flashcard from '../code/flashcards';
import CarouselCards from '../code/carouselCards';

export default function Search({ navigation }) {
  const [searchText, setSearchText] = React.useState('');
  const [searchResults, setSearchResults] = React.useState([]);

  const handleClear = () => {
    setSearchText('');
    setSearchResults([]);
  };

  const handleSearch = (query) => {
    setSearchText(query);
    filterDecks(query);
  };

  const filterDecks = (query) => {
    const filteredDecks = decks.filter((deck) =>
      deck.school.toLowerCase().includes(query.toLowerCase())
    );
    setSearchResults(filteredDecks);
  };

  const renderResultItem = ({ item }) => (
    <TouchableOpacity style={styles.resultItem} onPress={() => handleResultPress(item)}>
      <Text style={styles.resultItemText}>{item.school}</Text>
    </TouchableOpacity>
  );

  const handleResultPress = (item) => {
    // Handle navigation or other actions when a result is pressed
    console.log('Result Pressed:', item);
  };

  const uniqueSchools = Array.from(new Set(searchResults.map((deck) => deck.school)));

  return (
    <ImageBackground
      source={require('../assets/JungleBg.gif')}
      style={styles.SearchContainer}
    >
      <View style={{ flex: 1, alignItems: 'center' }}>
        <View style={styles.SearchBar}>
          <Icon name='search-outline' style={styles.searchIcon} />
          <TextInput
            style={{ flex: 1, margin: 10, marginLeft: 5, fontSize: 16 }}
            placeholder='Search'
            value={searchText}
            onChangeText={(text) => setSearchText(text)}
          />
          {searchText ? (
            <TouchableOpacity onPress={handleClear} style={styles.closeIconContainer}>
              <Icon name='close-outline' style={styles.closeIcon} />
            </TouchableOpacity>
          ) : null}
        </View>

        {searchResults.length > 0 ? (
          <FlatList
            data={uniqueSchools}
            renderItem={({ item }) => (
              <TouchableOpacity style={styles.resultItem} onPress={() => handleResultPress(item)}>
                <Text style={styles.resultItemText}>{item}</Text>
              </TouchableOpacity>
            )}
            keyExtractor={(item, index) => index.toString()}
          />
        ) : (
          <Text style={styles.l1}>Try searching your school!</Text>
        )}
      </View>
    </ImageBackground>
  );
}

const styles = StyleSheet.create({
  resultItem: {
    padding: 10,
    borderBottomWidth: 1,
    borderBottomColor: '#ccc',
  },
  resultItemText: {
    fontSize: 16,
    color: '#fff',
  },
  SearchContainer: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  SearchBar: {
    margin: 10,
    height: 50,
    width: '80%',
    top: -335,
    backgroundColor: '#ece3ce',
    borderRadius: 50,
    flexDirection: 'row',
    borderColor: '#000',
    borderWidth: 1,
    alignItems: 'center', 
    justifyContent: 'center'
  },
  searchIcon: {
    marginLeft: 10,
    marginRight: 5,
    color: '#527746',
    fontSize: 30,
  },
  closeIconContainer: {
    padding: 8,
  },
  closeIcon: {
    color: '#527746',
    fontSize: 30,
  },
  l1: {
    fontSize: 12,
    letterSpacing: 1,
    fontWeight: '700',
    color: '#ece3ce',
    textAlign: 'left',
    width: 295,
    height: 23,
    left: 20,
    margin: 10,
    top: -330,
    left: -25
  },
});
