import * as React from 'react';
import {  FlatList, View, StyleSheet, Text, TouchableOpacity, ScrollView,} from 'react-native';
import HomeScreen from './HomeScreen';
import LibrariesScreen from './LibrariesScreen';

export default function ViewScreen({navigation}){

   

    return(
      <View style = {styles.container}>
        <View>
          <Text>Subject</Text>
          <Text>Title</Text>
          <Text>Flashcard</Text>
          <TouchableOpacity>Mark as Favorite</TouchableOpacity>
        </View>
      </View>
          
    );
}

const styles = StyleSheet.create({

    scrollContainer:{
        flexGrow: 1,
        backgroundColor: 'rgba(0, 0, 0, 0)', 
        alignItems: 'center',
        justifyContent: 'center',
    },
    container: {
        flex: 1,
        backgroundColor: '#ECE3CE',
        alignItems: 'flex-start',
        justifyContent: 'flex-start',
        padding: 20,
    },
    flatlist: {
        width: 300,
        paddingBottom: 150
      },
      button: {
        backgroundColor: 'green',
        paddingVertical: 15,
        paddingHorizontal: 30,
        borderRadius: 5,
        margin: 10,
    },
    buttonText: {
      fontSize: 16,
      fontWeight: 'bold',
  },
})
