import * as React from 'react';
import {
  ImageBackground,
  ScrollView,
  StyleSheet,
  Text,
  TextInput,
  TouchableOpacity,
  View,
} from 'react-native';
import { Picker } from '@react-native-picker/picker';
import { decks } from '../code/data';

export default function Create({ navigation }) {
  const [title, setTitle] = React.useState('');
  const [subjectCode, setSubjectCode] = React.useState('');
  const [selectedCourse, setSelectedCourse] = React.useState('Actuarial Science');
  const [selectedSchool, setSelectedSchool] = React.useState('Adamson University');
  const [selectedCategory, setSelectedCategory] = React.useState('Business and Economics');

  // Dummy data, replace with your actual data
  const courses = [
    'Actuarial Science',
    'Applied Mathematics',
    'Chemical Engineering',
    'Computer Science',
    'Petroleum Engineering',
    'Physics',
  ];
  const schools = [
    'Adamson University',
    'Far Eastern University',
    'University of the East',
  ];
  const categories = [
    'Business and Economics',
    'Communications and Media',
    'Computer Science',
    'Engineering',
    'Health Sciences',
    'Mathematics and Statistics',
    'Natural Sciences',
    'Social Sciences',
    'Others',
  ];
  const AddDeck = () => {
    if (!title && !subjectCode && !selectedCourse && !selectedSchool && !selectedSchool) {
      alert('Please enter all the required values');
      return;
    }

    const newDeck = {
      id: decks.length + 1,
      category: selectedCategory,
      name: title,
      author: '@allen',
      code: subjectCode,
      course: selectedCourse,
      school: selectedSchool,
      created: 'yes',
      favorite: 'no',
      flashcards: [],
    };

    decks.unshift(newDeck);
    alert('Deck successfully added');
  };

  return (
    <ImageBackground source={require('../assets/JungleBg.gif')} style={styles.CreateContainer}>
      <ScrollView contentContainerStyle={styles.scrollContainer} showsVerticalScrollIndicator={false}>
        <Text style={styles.letsGetInto1}>Letâ€™s get into the (wood) work!</Text>

        <View style={styles.inputContainer}>
          <Text style={styles.label}>Title</Text>
          <TextInput
            style={styles.input}
            placeholder="Enter title"
            value={title}
            onChangeText={(text) => setTitle(text)}
          />
        </View>

        <View style={styles.inputContainer}>
          <Text style={styles.label}>Subject Code</Text>
          <TextInput
            style={styles.input}
            placeholder="Enter subject code"
            value={subjectCode}
            onChangeText={(text) => setSubjectCode(text.replace(/[^a-zA-Z0-9]/g, ''))}
          />
        </View>

        <View style={styles.inputContainer}>
          <Text style={styles.label}>Course</Text>
          <Picker
            style={styles.picker}
            selectedValue={selectedCourse}
            onValueChange={(itemValue, itemIndex) => setSelectedCourse(itemValue)}
          >
            {courses.map((course, index) => (
              <Picker.Item key={index} label={course} value={course} />
            ))}
          </Picker>
        </View>

        <View style={styles.inputContainer}>
          <Text style={styles.label}>School</Text>
          <Picker
            style={styles.picker}
            selectedValue={selectedSchool}
            onValueChange={(itemValue, itemIndex) => setSelectedSchool(itemValue)}
          >
            {schools.map((school, index) => (
              <Picker.Item key={index} label={school} value={school} />
            ))}
          </Picker>
        </View>

        <View style={styles.inputContainer}>
          <Text style={styles.label}>Category</Text>
          <Picker
            style={styles.picker}
            selectedValue={selectedCategory}
            onValueChange={(itemValue, itemIndex) => setSelectedCategory(itemValue)}
          >
            {categories.map((category, index) => (
              <Picker.Item key={index} label={category} value={category} />
            ))}
          </Picker>
        </View>

        <TouchableOpacity style={styles.createButton} onPress={() => AddDeck()}>
          <Text style={styles.createButtonText}>Create</Text>
        </TouchableOpacity>

        <Text style={styles.additionalTitle}>
          If your school is not listed as an option, please request its addition by contacting the administrators at grovecards@gmail.com.
        </Text>
      </ScrollView>
    </ImageBackground>
  );
}

const styles = StyleSheet.create({
  CreateContainer: {
    flex: 1,
    justifyContent: 'flex-start',
    resizeMode: 'cover',
  },
  scrollContainer: {
    flexGrow: 1,
    backgroundColor: 'rgba(0, 0, 0, 0)',
    paddingBottom: 200,
    paddingHorizontal: 10,
  },
  letsGetInto1: {
    top: 20,
    alignSelf: 'center',
    fontSize: 15,
    letterSpacing: 1.6,
    fontWeight: '700',
    color: '#fff',
    textAlign: 'center',
    height: 33,
    textShadowColor: 'rgba(0, 0, 0, 0.75)',
    textShadowOffset: { height: 1 },
    textShadowRadius: 10,
  },
  inputContainer: {
    top: 20,
    marginBottom: 20,
  },
  label: {
    fontSize: 16,
    color: '#ECE3CE',
    marginLeft: -5,
    marginBottom: 5,
    width: 'auto',
    textShadowColor: 'rgba(0, 0, 0, 0.75)',
    textShadowOffset: { width: -1, height: 1 },
    textShadowRadius: 10,
  },
  input: {
    height: 50,
    borderStyle: 'solid',
    borderColor: '#ece3ce',
    borderRadius: 5,
    borderWidth: 1,
    width: '100%',
    flexDirection: 'row',
    alignItems: 'center',
    paddingHorizontal: 10,
    paddingVertical: 5,
    color: '#fff',
  },
  picker: {
    height: 50,
    borderStyle: 'solid',
    borderColor: '#ece3ce',
    borderRadius: 10,
    borderWidth: 1,
    width: '100%',
    color: '#fff',
    paddingHorizontal: 10,
    paddingVertical: 5,
  },
  createButton: {
    backgroundColor: '#ece3ce',
    paddingHorizontal: 10,
    paddingVertical: 10,
    borderRadius: 20,
    width: 150,
    alignItems: 'center',
    alignSelf: 'flex-end',
    marginRight: 20,
    top: 30,
    shadowOffset: {
      width: 0,
      height: 4,
    },
    shadowRadius: 4,
    elevation: 4,
    shadowOpacity: 1,
  },
});
