import React, {useState} from 'react';
import {  FlatList, Image, View, StyleSheet, Text, TouchableOpacity, ScrollView,} from 'react-native';
import { decks } from '../code/data';
import { ITEM_WIDTH } from '../code/carouselCardItem';
export default function PreviewScreen({navigation}){
  const handleDeckPress = (deck) => {
    setSelectedDeck(deck);
  };

  const renderItem = ({ item }) => (
    <View style={styles.viewPadding}>
        <TouchableOpacity onPress={() => handleDeckPress(item)}style={styles.deckContainer}>
            <View style={styles.info}> 
                <Text style={styles.infotext}>{item.name}</Text>
                <View style={styles.info2}>
                    <Text style={styles.infotext2}>{item.items} items</Text>
                    <Text style={styles.infotext2}>{item.author}</Text>
                    <Text style={styles.infotext2}>{item.code}</Text>
                    <Text style={styles.infotext2}>{item.course}</Text>
                    <Text style={styles.infotext2}>{item.school}</Text>
                </View>
            </View>
        </TouchableOpacity>
    </View>
  );

    return(
      <View style = {styles.container}>
        <View>
          <Text>Subject</Text> 
          <Text>Title</Text>
          <Text>Flashcard</Text>
          <TouchableOpacity>Mark as Favorite</TouchableOpacity>
          <TouchableOpacity> go back</TouchableOpacity>
        </View>

        <View style = {styles.scrollContainer}>
           <FlatList 
           style={styles.flatlist} 
           showsVerticalScrollIndicator={false}
           data={decks}
            renderItem={renderItem} //note: idk if there's an existing deck of cards but just replace the contents with em on 
            />
            <FlatList 
           style={styles.flatlist} 
           showsVerticalScrollIndicator={false}
           data={decks}
            renderItem={renderItem} //note: idk if there's an existing deck of cards but just replace the contents with em on 
            />
        </View>
      </View>
          
    );
}

const styles = StyleSheet.create({

    scrollContainer:{
        flexGrow: 1,
        backgroundColor: 'rgba(0, 0, 0, 0)', 
        alignItems: 'center',
        justifyContent: 'center',
    },
    container: {
        flex: 1,
        backgroundColor: '#ECE3CE',
        alignItems: 'flex-start',
        justifyContent: 'flex-start',
        padding: 20,
    },
    flatlist: {
        width: 300,
        paddingBottom: 150
      },
      button: {
        backgroundColor: 'green',
        paddingVertical: 15,
        paddingHorizontal: 30,
        borderRadius: 5,
        margin: 10,
    },
    buttonText: {
      fontSize: 16,
      fontWeight: 'bold',
    },
    viewPadding: {
      padding: 10,
    }, 
    flatlist: {
      width: 300,
      paddingBottom: 150
    },
    deckContainer: {
      width: ITEM_WIDTH,
      height: 170, // Set height equal to width
      marginHorizontal: 8, // Adjust margin as needed
      borderRadius: 8,
      backgroundColor: '#C9755E',
      shadowColor: '#000',
      shadowOffset: {
        width: 0,
        height: 2,
      },
      shadowOpacity: 0.25,
      shadowRadius: 3.84,
      elevation: 5,
    },
    info: {
      flex: 1,
      padding: 10,
      top: 5,
      position: 'absolute'
    },
    info2: {
      flex: 1,
      padding: 10,
      top: 70,
      left: 0,
      position: 'absolute'
    },
    infotext: {
      fontSize: 20,
      fontWeight: 'bold',
      color: '#4F6F52',
    },
    infotext2: {
      fontSize: 10,
      color: '#4F6F52',
    },
    flash: {
      width: 200,
      height: 170,
      position: 'absolute',
      flex: 1,
      left: 50, 
      alignContent: 'flex-end'
    },
    scrollContainer:{
      flexDirection: 'row',
      backgroundColor: 'rgba(0, 0, 0, 0)', 
      alignItems: 'center',
      justifyContent: 'center',
  },
})
